import LazyComponent from"./LazyComponent.js";import{onEvent}from"../utils/element.js";export default class FocusCapture extends LazyComponent{constructor(){super({delegatesFocus:!0})}render(){super.render(),onEvent(this.shadowRoot.getElementById("after"),"focus",(()=>{this.querySelector('button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])').focus()}))}get shadowTemplate(){return`\n      ${super.shadowTemplate}\n      <div id="after" tabindex="0"></div>\n    `}get shadowStyles(){return"\n      :host {\n        display: inline-block;\n      }\n    "}}window.customElements.define("k-focus-capture",FocusCapture);