export const fuzzyMatch=(t,r)=>{const e=((t,r)=>{const e=t.length,a=r.length,n=Array.from({length:e+1},(()=>Array(a+1).fill(0)));for(let h=0;h<=e;h++)for(let e=0;e<=a;e++)n[h][e]=0===h?e:0===e?h:Math.min(n[h-1][e-1]+(t.charAt(h-1)===r.charAt(e-1)?0:1),n[h][e-1]+1,n[h-1][e]+1);return n[e][a]})(t.toLowerCase(),r.toLowerCase());if(0===e)return 10;const a=Math.max(0,10-e/3),n=Math.min((t.length-e)/t.length,1);return Math.round(5*a+5*n)};export const fuzzyMatchArray=(t,r)=>{const e=t.map((t=>fuzzyMatch(t,r))).filter((t=>0!==t));if(0===e.length)return 0;return e.reduce(((t,r)=>t+r),0)/e.length};export default{fuzzyMatch:fuzzyMatch,fuzzyMatchArray:fuzzyMatchArray};